import React, { useState } from "react";
import axios from "axios";

const Form = () => {
  const [teste, setTeste] = useState({
    name: "",
    age: "",
    contact: "",
    reason: "",
    hour: "",
    date: new Date(),
  });

  //Substituir pelo MONGODB?
  const [novaMarcacao, setNovaMarcacao] = useState([]);

  //events handlers
  const handleChange = (e) => {
    const name = e.target.name;
    const value = e.target.value;

    setTeste({ ...teste, [name]: value });
  };
  const handleSintomas = (e) => {
    const value = e.target.value;

    console.log(value);

    setTeste({ ...teste, reason: value });
  };
  const handleHours = (e) => {
    const value = e.target.value;

    console.log(value);

    setTeste({ ...teste, hour: value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    const newTeste = { ...teste, id: new Date().getTime().toString() };
    setNovaMarcacao([...novaMarcacao, newTeste]);

    axios.post("http://localhost:5000/marcacoes/add", newTeste).then((res) => {
      console.log("hello");
      console.log(res.data);
    });

    setTeste({
      name: "",
      age: "",
      contact: "",
      reason: "",
      hour: "",
      date: new Date(),
    });
  };
  return (
    <>
      <div className="form-group">
        <form>
          <div className="form-element">
            <label htmlFor="name">Nome</label>
            <input
              type="text"
              id="name"
              name="name"
              value={teste.name}
              onChange={handleChange}
            />
          </div>
          <div className="form-element">
            <label htmlFor="age">Idade</label>
            <input
              type="text"
              id="age"
              name="age"
              value={teste.age}
              onChange={handleChange}
            />
          </div>
          <div className="form-element">
            <label htmlFor="contact">Contacto</label>
            <input
              type="text"
              id="contact"
              name="contact"
              value={teste.contact}
              onChange={handleChange}
            />
          </div>

          <div className="form-element">
            <label htmlFor="reason">Sintomas</label>
            <select onChange={handleSintomas}>
              <option name="reason">Sintomas</option>
              <option name="reason">Contacto Proximo</option>
            </select>
          </div>

          <div className="form-element">
            <label htmlFor="marcacao">Nome</label>
            <input
              type="date"
              id="date"
              name="date"
              value={teste.date}
              onChange={handleChange}
            />
          </div>

          <div className="form-element">
            <label htmlFor="hours">Horas</label>
            <select id="hours" onChange={handleHours}>
              <option value="10:00">10:00</option>
              <option value="10:20">10:20</option>
              <option value="10:40">10:40</option>
              <option value="11:00">11:00</option>
            </select>
          </div>

          <div className="btn-container">
            <button
              className="btn btn-primary btn-lg"
              type="submit"
              onClick={handleSubmit}
            >
              Confirmar Marcação
            </button>
          </div>
        </form>
      </div>
    </>
  );
};

export default Form;
