import React, { useState } from "react";
import axios from "axios";

const Form = () => {
  const [teste, setTeste] = useState({
    name: "",
    age: "",
    contact: "",
    reason: "",
    date: new Date(),
  });

  //Substituir pelo MONGODB?
  const [novaMarcacao, setNovaMarcacao] = useState([]);

  //events handlers
  const handleChange = (e) => {
    const name = e.target.name;
    const value = e.target.value;

    setTeste({ ...teste, [name]: value });
  };
  const handleSintomas = (e) => {
    const value = e.target.value;

    console.log(value);

    setTeste({ ...teste, reason: value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    const newTeste = { ...teste, id: new Date().getTime().toString() };
    setNovaMarcacao([...novaMarcacao, newTeste]);

    axios.post("http://localhost:5000/marcacoes/add", newTeste).then((res) => {
      console.log("hello");
      console.log(res.data);
    });

    setTeste({
      name: "",
      age: "",
      contact: "",
      reason: "",
      date: new Date(),
    });
  };
  return (
    <>
      <div className="form-container">
        <form>
          <div className="form-element">
            <label htmlFor="name">Nome</label>
            <input
              type="text"
              id="name"
              name="name"
              value={teste.name}
              onChange={handleChange}
            />
          </div>
          <div className="form-element">
            <label htmlFor="age">Idade</label>
            <input
              type="text"
              id="age"
              name="age"
              value={teste.age}
              onChange={handleChange}
            />
          </div>
          <div className="form-element">
            <label htmlFor="contact">Contacto</label>
            <input
              type="text"
              id="contact"
              name="contact"
              value={teste.contact}
              onChange={handleChange}
            />
          </div>
          {/* <div>
            <label htmlFor="reason">reason</label>
            <input
              type="text"
              id="reason"
              name="reason"
              value={teste.reason}
              onChange={handleChange}
            />
          </div> */}
          <div className="form-element">
            <label htmlFor="reason">Sintomas</label>
            <select onChange={handleSintomas}>
              <option name="reason">Sintomas</option>
              <option name="reason">Contacto Proximo</option>
            </select>
          </div>

          <div className="form-element">
            <label htmlFor="marcacao">Nome</label>
            <input
              type="date"
              id="date"
              name="date"
              value={teste.date}
              onChange={handleChange}
            />
          </div>

          {/* <select id="hours">
            <option value="10.00">10.00</option>
            <option value="10.20">10.20</option>
            <option value="10.40">10.40</option>
            <option value="11">11</option>
          </select> */}

          <button type="submit" onClick={handleSubmit}>
            Confirmar Marcação
          </button>
        </form>
      </div>
    </>
  );
};

export default Form;
