import axios from "axios";
import React, { useState, useEffect } from "react";

const Home = () => {
  const [futurasMarcacoes, setFuturasMarcacoes] = useState([]);

  useEffect(() => {
    getMarcacoes();
  }, []);

  const getMarcacoes = () => {
    axios.get("http://localhost:5000/marcacoes").then((res) => {
      const marcacoes = res.data;
      setFuturasMarcacoes(marcacoes);
    });
  };

  return (
    <>
      <h2>Home</h2>
      <article>
        {futurasMarcacoes.map((cenas, index) => {
          const { name, age, contact, reason, date } = cenas;

          return (
            <div key={index}>
              <h4>Nome: {name}</h4>
              <p>Idade: {age}</p>
              <p>Contacto: {contact}</p>
              <p>Raz√£o: {reason}</p>
              <p>Data: {date}</p>
            </div>
          );
        })}
      </article>
    </>
  );
};

export default Home;
