import React, { useState, useEffect } from "react";
import axios from "axios";

const Form = () => {
  const [teste, setTeste] = useState({
    name: "",
    age: "",
    contact: "",
    reason: "",
    hour: "",
    date: "",
  });
  const [hours, setHours] = useState([
    "10:00",
    "10:20",
    "10:40",
    "11:00",
    "11:20",
    "11:40",
    "12:00",
  ]);

  //09:20 ate as 16:40
  const availableHours = ["10:00", "10:20", "10:40", "11:00"];

  // useEffect(() => {
  //   getHours();
  // }, []);

  // const getHours = () => {
  //   axios.get("http://localhost:5000/marcacoes").then((res) => {
  //     console.log("Horas");
  //     const marcacoes = res.data;
  //     console.log(marcacoes);

  //     const diaMarcacao = marcacoes.filter((data) => data.date === teste.date);

  //     console.log("DIA");
  //     console.log(diaMarcacao);

  //     const horasIndisp = [];
  //     var horasDisp = [];

  //     diaMarcacao.map((hour) => horasIndisp.push(hour.hour));
  //     console.log("Horas INDISPONIVEIS");
  //     console.log(horasIndisp);

  //     horasDisp = hours.filter((e) => !horasIndisp.includes(e));

  //     console.log("Horas DISPONIVEIS");
  //     console.log(horasDisp);
  //     // setHours([horasDisp]);
  //   });
  // };

  //Substituir pelo MONGODB?
  const [novaMarcacao, setNovaMarcacao] = useState([]);

  //events handlers
  const handleChange = (e) => {
    const name = e.target.name;
    const value = e.target.value;

    setTeste({ ...teste, [name]: value });
  };
  const handleDate = (e) => {
    const name = e.target.name;
    const value = e.target.value;

    // setTeste({ ...teste, [name]: value });
    axios
      .get("http://localhost:5000/marcacoes")
      .then(setTeste({ ...teste, [name]: value }))
      .then((res) => {
        console.log("Horas");
        const marcacoes = res.data;
        console.log(marcacoes);

        const diaMarcacao = marcacoes.filter(
          (data) => data.date === teste.date
        );

        console.log(teste.date);

        console.log("DIA");
        console.log(diaMarcacao);

        const horasIndisp = [];
        var horasDisp = [];

        diaMarcacao.map((hour) => horasIndisp.push(hour.hour));
        console.log("Horas INDISPONIVEIS");
        console.log(horasIndisp);

        horasDisp = hours.filter((e) => !horasIndisp.includes(e));

        console.log("Horas DISPONIVEIS");
        console.log(horasDisp);
        setHours(horasDisp);
      });
  };

  const handleSintomas = (e) => {
    const value = e.target.value;

    console.log(value);

    setTeste({ ...teste, reason: value });
  };

  const handleHours = (e) => {
    const value = e.target.value;

    console.log(value);
    console.log(teste.date);

    setTeste({ ...teste, hour: value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    const newTeste = { ...teste, id: new Date().getTime().toString() };
    setNovaMarcacao([...novaMarcacao, newTeste]);

    axios.post("http://localhost:5000/marcacoes/add", newTeste).then((res) => {
      console.log("hello");
      console.log(res.data);
    });

    setTeste({
      name: "",
      age: "",
      contact: "",
      reason: "",
      hour: "",
      date: new Date(),
    });
  };

  return (
    <>
      <div className="form-group">
        <form>
          <div className="form-element">
            <label htmlFor="name">Nome</label>
            <input
              type="text"
              id="name"
              name="name"
              value={teste.name}
              onChange={handleChange}
            />
          </div>
          <div className="form-element">
            <label htmlFor="age">Idade</label>
            <input
              type="text"
              id="age"
              name="age"
              value={teste.age}
              onChange={handleChange}
            />
          </div>
          <div className="form-element">
            <label htmlFor="contact">Contacto</label>
            <input
              type="text"
              id="contact"
              name="contact"
              value={teste.contact}
              onChange={handleChange}
            />
          </div>

          <div className="form-element">
            <label htmlFor="reason">Motivo</label>
            <select onChange={handleSintomas}>
              <option name="reason">Sintomas</option>
              <option name="reason">Contacto Proximo</option>
            </select>
          </div>

          <div className="form-element">
            <label htmlFor="marcacao">Data</label>
            <input
              type="date"
              id="date"
              name="date"
              value={teste.date}
              onChange={handleDate}
            />
          </div>

          <div className="form-element">
            <label htmlFor="hours">Horas</label>
            <select id="hours" onChange={handleHours}>
              {hours.map((hour) => {
                return (
                  <option key={hour} value={hour}>
                    {hour}
                  </option>
                );
              })}
            </select>
          </div>

          <div className="btn-container">
            <button className="button" type="submit" onClick={handleSubmit}>
              Confirmar Marcação
            </button>
          </div>
        </form>
      </div>
    </>
  );
};

export default Form;
