import axios from "axios";
import React, { useState, useEffect } from "react";
import { FaTrash, FaEdit } from "react-icons/fa";

const Home = () => {
  const [futurasMarcacoes, setFuturasMarcacoes] = useState([]);
  const [teste, setTeste] = useState({
    name: "",
    age: "",
    contact: "",
    reason: "",
    hour: "",
    date: "",
  });

  useEffect(() => {
    getMarcacoes();
  }, []);

  const getMarcacoes = () => {
    axios.get("http://localhost:5000/marcacoes").then((res) => {
      const marcacoes = res.data;

      marcacoes.sort((a, b) => {
        return new Date(a.date).getTime() - new Date(b.date).getTime();
      });
      setFuturasMarcacoes(marcacoes);
    });
  };

  return (
    <>
      <h2>Home</h2>
      <div>
        {futurasMarcacoes.map(
          ({ _id, name, age, contact, reason, date, hour }) => {
            return (
              <div key={_id} className="marcacao">
                <h4>
                  Nome: <span>{name}</span>
                </h4>
                <p>Idade: {age}</p>
                <p>Contacto: {contact}</p>
                <p>Raz√£o: {reason}</p>
                <p>Data: {date}</p>
                <p>Hora: {hour}</p>
                <FaTrash
                  style={{ color: "red", cursor: "pointer", margin: "1.5em" }}
                />
                <FaEdit
                  style={{ color: "blue", cursor: "pointer", margin: "1.5em" }}
                />
              </div>
            );
          }
        )}
      </div>
    </>
  );
};

export default Home;
