import React, { useState } from "react";

const Form = () => {
  const [teste, setTeste] = useState({
    name: "",
    age: "",
    contact: "",
    reason: "",
    date: new Date(),
  });

  //Substituir pelo MONGODB?
  const [novaMarcacao, setNovaMarcacao] = useState([]);

  const handleChange = (e) => {
    const name = e.target.name;
    const value = e.target.value;
    setTeste({ ...teste, [name]: value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    const newTeste = { ...teste, id: new Date().getTime().toString() };
    setNovaMarcacao([...novaMarcacao, newTeste]);
  };
  return (
    <>
      <div className="form-container">
        <form>
          <div>
            <label htmlFor="name">Nome</label>
            <input
              type="text"
              id="name"
              name="name"
              value={teste.name}
              onChange={handleChange}
            />
          </div>
          <div>
            <label htmlFor="age">Idade</label>
            <input
              type="text"
              id="age"
              name="age"
              value={teste.age}
              onChange={handleChange}
            />
          </div>
          <div>
            <label htmlFor="contact">Contacto</label>
            <input
              type="text"
              id="contact"
              name="contact"
              value={teste.contact}
              onChange={handleChange}
            />
          </div>
          <div>
            <label htmlFor="reason">Sintomas</label>
            <input
              type="text"
              id="reason"
              name="reason"
              value={teste.reason}
              onChange={handleChange}
            />
            {/* <label htmlFor="contactoRisco">Contacto Risco</label>
              <input
              type="checkbox"
              id="contactoRisco"
              name="contactoRisco"
              value={teste.name}
            /> */}
          </div>
          <div>
            <label htmlFor="marcacao">Nome</label>
            <input
              type="date"
              id="date"
              name="date"
              value={teste.date}
              onChange={handleChange}
            />
          </div>
          <button type="submit" onClick={handleSubmit}>
            Confirmar Marcação
          </button>
        </form>
      </div>
    </>
  );
};

export default Form;
